mapper.select:
SELECT COUNT(*)
FROM bill_record
WHERE group_id = #{groupId}

    AND record_time >= #{fromDate} AND record_time  <= #{toDate}


    AND creator_name = #{limitName}


    AND type_id =#{limitTypeId}


    AND amount > 0


    AND amount  <0

SELECT SUM(amount)
FROM bill_record
WHERE group_id = #{groupId}

    AND record_time >= #{fromDate} AND record_time  <= #{toDate}


    AND creator_name = #{limitName}


    AND type_id =#{limitTypeId}


    AND amount > 0


    AND amount  <0

SELECT r_id, group_id, type_id, name, record_time, amount, information, creator_id, creator_name, create_time, status
FROM bill_record
WHERE group_id = #{groupId}

    AND record_time >= #{fromDate} AND record_time  <= #{toDate}


    AND creator_name = #{limitName}


    AND type_id =#{limitTypeId}


    AND amount > 0


    AND amount  <0

LIMIT #{start}, #{size}

SELECT type_id, SUM(amount) as total_amount
FROM bill_record
WHERE group_id = #{groupId}

    AND record_time >= #{fromDate} AND record_time  <= #{toDate}


    AND creator_name = #{limitName}


    AND type_id =#{limitTypeId}


    AND amount > 0


    AND amount  <0

GROUP BY type_id

SELECT type_id, record_time, SUM(amount) as total_amount
FROM bill_record
WHERE group_id = #{groupId}

    AND record_time >= #{fromDate} AND record_time  <= #{toDate}


    AND creator_name = #{limitName}


    AND type_id =#{limitTypeId}


    AND amount > 0


    AND amount  <0

GROUP BY type_id, record_time
